import React ,{ useState, useEffect} from "react";
import  Chart  from "react-apexcharts";
function Piechart()
{
   const [stdudentSubject, setStudentsubject]= useState([]);
   const [studentMarks, setStudentMarks]= useState([]);
   useEffect( ()=>{
       const sSubject=[];
       const sMarks=[];
       const getStudentdata= async()=>{
       const reqData= await fetch("http://localhost/reactgraphtutorial/marks");
       const resData= await reqData.json();       
       for(let i=0; i< resData.length; i++)
       {
        sSubject.push(resData[i].subject);
        sMarks.push(parseInt(resData[i].marks));
       }
       setStudentsubject(sSubject);
       setStudentMarks(sMarks);
        //console.log(resData); 
       }
    getStudentdata();
   },[]);
    return(<React.Fragment><div className="container-fluid mb-3"><h3 className="mt-3">Welcome to Piechart </h3><Chart 
                type="pie"
                width={1349}
                height={550}
                series={ studentMarks }                
                options={{
                        title:{ text:"Student PieChart"
                        } , 
                       noData:{text:"Empty Data"},                        
                      // colors:["#f90000","#f0f"],
                      labels:stdudentSubject                     
                 }}></Chart></div></React.Fragment>
    );
}
export default Piechart;

