import React, { useState } from "react";
import { Pie } from "react-chartjs-2";
import { Chart, registerables,ArcElement } from "chart.js";
Chart.register(...registerables);
Chart.register(ArcElement);

const PieChart = () => {
  const [data, setData] = useState({
    labels: ["State 1", "State 2", "State 3", "State 4", "State 5", "State 6" , "State 7", "State 8", "State 9", "State 10"],
    datasets: [
      {
        data: ["User 1", "User 2", "User 3", "User 4","User 5","User 6","User 7","User 8"],
        backgroundColor: [
          "rgba(255, 99, 132)",
          "rgba(54, 162, 235)",
          "rgba(255, 206, 86)",
          "rgba(75, 192, 192)",
          "rgba(153, 102, 255)",
          "rgba(60, 179, 113)",
          "rgba(210, 210, 210)",
          "rgba(106, 90, 205)",
          "rgba(255, 99, 71)",
          "rgba(255, 165, 0)",

        ],
        hoverBackgroundColor: [
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
        ],
      },
    ],
  });

  return (
    <div className="PieChart" style={{ height: '30%' , width:'30%'}}>
      <Pie data={data} options={{ maintainAspectRatio: true }} />
    </div>
  );
};



