import { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import API from "./API";



const Additems = ({ onAdd }) => {
  const[selectItems, setSelectitems]= useState([]);
  const [itemId, setItemId] = useState(null);
  const [itemnumber, setItemnumber] = useState("");
  const [featurename, setFeaturename] = useState("");
  const [featurestate, setFeaturestate] = useState("");
  const [assignuser, setAssignuser] = useState("");
  const [testexecuted, setTestexecuted] = useState(null);
  const [testpassed, setTestpassed] = useState(null);
  const [testfailed, setTestfailed] = useState(null);


  useEffect(() => {
    refreshItems();
  }, []);

 const refreshItems = () => {
  console.log("refreshItems")
    API.get("/")
      .then((res) => {
        console.log("res",res)
        setSelectitems(res.data);
        // setItemnumber(res.data);
        // setItemnumber(res[0].itemnumber);
        // setFeaturename(res[0].featurename);
        // setFeaturestate(res[0].featurestate);
        // setItemId(res[0].id);
        // setTestfailed(res[0].testfailed);
        // setAssignuser(res[0].assignuser);
        // setTestpassed(res[0].testpassed);
        // setTestexecuted(res[0].testexecuted);
      })
      .catch(error => {
        console.log("error",error)
        console.error()
      });
  }

  const onSubmit = (e) => {
    e.preventDefault();
    let item = { itemnumber, featurename, featurestate, assignuser, testexecuted, testpassed, testfailed };
    API.post("/", item).then(() => refreshItems());
  }

  const onUpdate = (id) => {
    let item = { itemId };
    API.patch(`/${id}/`, item).then((res) => refreshItems());
  }

  const onDelete = (id) => {
    API.delete(`/${id}/`).then((res) => refreshItems());
  }

  function getselectItems(id) {
    let item = item.filter((Items) => Items.id === id)[0];
    setItemnumber(item.number);
    setFeaturename(item.name);
    setFeaturestate(item.state);
    setAssignuser(item.user);
    setTestexecuted(item.testexecuted);
    setTestpassed(item.testpassed);
    setTestfailed(item.testfailed);
    setItemId(item.itemId);
  }

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-4">
          <h3 className="float-left">Create a new Items</h3>
          <Form onSubmit={onSubmit} className="mt-4">
            <Form.Group className="mb-3" controlId="formBasicItemnumber">
              <Form.Label>{itemId}Item umber</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Number"
                value={itemnumber}
                onChange={(e) => setItemnumber(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicFeaturename">
              <Form.Label>Feature Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter name"
                value={featurename}
                onChange={(e) => setFeaturename(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicFeaturestate">
              <Form.Label>Feature State</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter State"
                value={featurestate}
                onChange={(e) => setFeaturestate(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicAssignuser">
              <Form.Label>Assigned User</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter User"
                value={assignuser}
                onChange={(e) => setAssignuser(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicTestexecuted">
              <Form.Label>TotalTest Executed</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter testexecuted"
                value={testexecuted}
                onChange={(e) => setTestexecuted(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicTestpassed">
              <Form.Label>testpassed</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter testpassed"
                value={testpassed}
                onChange={(e) => setTestpassed(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicTestfailed">
              <Form.Label>testfailed</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter testfailed"
                value={testfailed}
                onChange={(e) => setTestfailed(e.target.value)} />
            </Form.Group>

            <div className="float-right">
              <Button
                variant="primary"
                type="submit"
                onClick={onSubmit}
                className="mx-2"
              >
                Save
              </Button>
              <Button
                variant="primary"
                type="button"
                onClick={() => {onUpdate(itemId)}}
                className="mx-2"
              >
                Update
              </Button>
            </div>
          </Form>
        </div>
        <div className="col-md-8 m">
          <table class="table">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Item Number</th>
                <th scope="col">Feature Name</th>
                <th scope="col">Feature State</th>
                <th scope="col">Assign User</th>
                <th scope="col">Test Executed</th>
                <th scope="col">Test Passed</th>
                <th scope="col">Test Failed</th>
                <th scope="col"></th>
              </tr>
            </thead>
            <tbody>

              {selectItems.map((item, index) => {
                console.log("item",item)
                return (
                  <tr key="">
                    <th scope="row">{item.id}</th>
                    <td> {item.item_number}</td>
                    <td>{item.feature_name}</td>
                    <td>{item.feature_state}</td>
                    <td>{item.assigned_user}</td>
                    <td>{item.total_test_excuted}</td>
                    <td>{item.test_passed}</td>
                    <td>{item.test_failed}</td>
                    <td>
                      <i
                        className="fa fa-pencil-square text-primary d-inline"
                        aria-hidden="true"
                        onClick={() => selectItems(item.id)}
                      ></i>
                      <i
                        className="fa fa-trash-o text-danger d-inline mx-3"
                        aria-hidden="true"
                        onClick={() => onDelete(item.id)}
                      ></i>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Additems;
