import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js/auto";
import Chart from 'react-apexcharts';

function BarChart({ chartData }) {
  const StackedBarChart = () => {
  const [data, setData] = useState({
    labels: ["User 1", "User 2", "User 3", "User 4","User 5","User 6","User 7","User 8",],
    datasets: [ 
      {
        label:'# of votes',
        data: [5, 8, 4, 4, 8, 11, 2, 7, 5, 6],
        backgroundColor: [
          "rgba(255, 99, 132)",
          "rgba(54, 162, 235)",
          "rgba(255, 206, 86)",
          "rgba(75, 192, 192)",
          "rgba(153, 102, 255)",
          "rgba(60, 179, 113)",
          "rgba(210, 210, 210)",
          "rgba(106, 90, 205)",
          "rgba(255, 99, 71)",
          "rgba(255, 165, 0)",

        ],
        hoverBackgroundColor: [
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
          "rgb(255,255,255)",
        ],
      },
      /*{
        label: "State 1", 
        backgroundColor: "#FF6384",
        data: [4, 5, 4, 3, 2 , 11, 5, 8, 5, 6],
      
        label: "State 2",
        backgroundColor: "#36A2EB",
        data: [2, 3, 2, 5, 6, 7, 5, 6, 2, 1],
      
        label: "State 3",
        backgroundColor: "#f3ba2f",
        data: [3, 2, 3, 7, 2, 5, 1, 4, 3, 4],
     
        label: "State 4",
        backgroundColor: "#2a71d0",
        data: [1, 6, 5, 2, 1, 5, 8, 3, 4, 1],
     
        label: "State 5",
        backgroundColor: "#ee82ee",
        data: [1, 6, 7, 2, 1, 6, 8, 2, 7, 1],
      
        label: "State 6",
        backgroundColor: "#3cb371",
        data: [1, 4, 5, 7, 1, 4, 5, 7, 3, 9],
      
        label: "State 7",
        backgroundColor: "#6a5acd",
        data: [7, 2, 7, 2, 1, 5, 4, 8, 3, 6],
      
        label: "State 8",
        backgroundColor: "#ffa500",
        data: [3, 6, 4, 1, 1, 6, 4, 2, 8, 3],
      
        label: "State 9",
        backgroundColor: "#616161",
        data: [1, 7, 5, 4, 1, 5, 7, 2, 4, 6],
     
        label: "State 10",
        backgroundColor: "#b4b4b4",
        data: [ 6, 8, 4, 2, 9, 7, 3, 4, 1, 7],
      },*/      
    ],
  });

  const options = {
    scales: {
      xAxes: [
        {
          stacked: true,
        },
      ],
      yAxes: [
        {
          ticks: {beginATZero: true },
          stacked: true,
        },
      ],
    },
  };

  return (
    <div>
      <StackedBar data={data}  height={50} options={options} />
    </div>
  );
}
};

export default StackedBarChart;




