const Dropdown = ({ placeHolder, options, isMulti }) => {
    const [showMenu, setShowMenu] = useState(false);
    const [selectedValue, setSelectedValue] = useState(isMulti ? [] : null);

    useEffect( () => {
        const handleInputClick =(e) => {};

        const getDisplay = () => {
            if (!selectedValue || selectedValue.length === 0) {
                return placeHolder;
            }
            if (isMulti) {
                return (
                    <div className="dropdown-tags">
                        {selectedValue.map((option) => (
                            <div key={option.value} className="dropdown-tag-item">
                                {option.label}
                                <span onClick={(e) => onTagRemove(e, option)} className="dropdown-tag-close">dropdown-tag-close</span>
                            </div>
                        ))}
                    </div>
                );
            }
            return selectedValue.label;
        };
        const removeOption = (option) => {
            return selectedValue.filter((o) => o.value !== option.value);
        };
        const onTagRemove = (e, option) => {
            e.stopPropagation();
            setSelectedValue(removeOption(option));
        };

        const onItemClick = (option) => {
            let newValue;
            if (isMulti) {
                if (selectedValue.findIndex((o) => o.value === option.value) >= 0) {
                    newValue = removeOption(option);
                } else {
                    newValue = [...selectedValue, option];
                }
            } else {
                newValue = option;
            }
            setSelectedValue(newValue);
        };

        const isSelected = (option) => {
            if (isMulti) {
                return selectedValue.filter((o) => o.value === option.value).length > 0;
            }
            if (!selectedValue) {
                return false;
            }
            return selectedValue.value === option.value;
        };
    }

    )

  return (
    <div className="dropdown-container">
      <div className="dropdown-input">
        <div className="dropdown-selected-value">select</div>
        <div className="dropdown-tools">
          <div className="dropdown-tool">
            <Icon />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dropdown;






