import React, { useState } from "react";
import {
  chart as ChartJS,
  categoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js'
import { Bar } from "react-chartjs-2";

const StackedBarChart = () => {
  const [data, setData] = useState({
    labels: ["User 1", "User 2", "User 3", "User 4","User 5","User 6","User 7","User 8"],
    datasets: [ 
      
      {
        label: "State 1", 
        backgroundColor: "#FF6384",
        data: [4, 5, 4, 3, 2],
      },
      {
        label: "State 2",
        backgroundColor: "#36A2EB",
        data: [2, 3, 2, 5, 6, 7, 5],
      },
      {
        label: "State 3",
        backgroundColor: "#f3ba2f",
        data: [3, 2, 3, 7, 2, 5, 1, 4, 3, 4],
      },
      {
        label: "State 4",
        backgroundColor: "#2a71d0",
        data: [1],
      },
      {
        label: "State 5",
        backgroundColor: "#ee82ee",
        data: [1],
      },
      {
        label: "State 6",
        backgroundColor: "#3cb371",
        data: [1, 4, 5, 7, 1, 4, 5, 7, 3, 9],
      },
      {
        label: "State 7",
        backgroundColor: "#6a5acd",
        data: [7, 2, 7, 2, 1, 5, 4, 8, 3, 6],
      },
      {
        label: "State 8",
        backgroundColor: "#ffa500",
        data: [3, 6, 4, 1, 1, 6, 4, 2, 8, 3],
      },
      {
        label: "State 9",
        backgroundColor: "#616161",
        data: [1, 7, 5, 4, 1, 5, 7, 2, 4, 6],
      },
      {        
        label: "State 10",
        backgroundColor: "#b4b4b4",
        data: [ 6, 8, 4, 2, 9, 7, 3, 4, 1, 7],

      },      
    ],
  });

  const options = {
    scales: {
      xAxes: [
        {
          stacked: true,
        },
      ],
      yAxes: [
        {
          ticks: {beginATZero: true },
          stacked: true,
        },
      ],
    },
  };

  return (
    <div>
      <Bar data={data}  height={0} options={options} />
    </div>
  );
};

export default StackedBarChart;




